import React, { useState, useEffect } from 'react';

import api from './services/api';

function App() {
  const [express, setExpress] = useState({});

  async function getInfo() {
    const response = await api('http://localhost:5000/api/mensagem', {
      method: 'POST',
      body: {
        message: 'Eu como merda'
      }
    });

    if (response.status !== 200) throw Error('Error');

    return setExpress(response.data);
  }

  useEffect(() => {
    getInfo();
  }, []);

  return (
    <div className="App">
      Front-end: Hello World! <br />
      Back-end: {express.express}
    </div>
  );
}

export default App;
