import React from 'react';

import './styles.css';

function Container() {

  const [express, setExpress] = useState({});
  const [id, setId] = useState('');

  async function getInfo() {
    const response = await api('/');

    if (response.status !== 200) throw Error('Error');

    return setExpress(response.data);
  }

  async function sendVideoInfos() {
    const response = await api('/', {
      method: 'POST',
      data: {
        id
      },
    });

    window.location = response.data;
  }

  useEffect(() => {
    getInfo();
  }, []);


  return (
    <div className="container">
      <h1>Instagram</h1>
      <input type="text" placeholder="Digite o ID do vídeo" onChange={(e) => { return setId(e.target.value) }} />
      <input type="button" onClick={sendVideoInfos} value="Clique aqui" /> <br />
      <h5>Back-end: {express.express === 'On-line' ? express.express : 'Off-line'}</h5>
    </div>
  );
}

export default Container;
